package kwikimport grails.test.mixin.*import java.lang.invoke.MethodHandleImpl.BindCaller.Timport spock.lang.Specification@Mock(User)
class SignupControllerSpec extends Specification {	def "should create an user"() {		when:		controller.create("user@email.com", "1234")				then:		def user = User.findByUsername("user@email.com")				and:		user.username == "user@email.com"		user.password == "1234"	}		def "should thrown an exception if ther username is empty"() {		when:		controller.create(null, "1234")				then:		thrown Exception	}		def "should thrown an exception if ther password is empty"() {		when:		controller.create("username@email.com", null)				then:		thrown Exception	}}
